import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';


function Testimonials() {
  const { ref, inView } = useInView({
    triggerOnce: true, // Animasyonu sadece bir kez tetikle
    threshold: 0.2,    // Elemanın %20'si görünür olduğunda tetikle
  });

  const testimonials = [
    { name: 'John Doe', feedback: 'Excellent service!' },
    { name: 'Jane Smith', feedback: 'Highly recommend this company.' },
  ];

  return (
    <section id="testimonials" className="testimonials" ref={ref}>
      <h2>What Our Clients Say</h2>
      <div>
        {testimonials.map((testimonial, index) => (
          <motion.div
            key={index}
            className="testimonial"
            initial={{ opacity: 0, y: 50 }}
            animate={inView ? { opacity: 1, y: 0 } : {}} // Görünümdeyse animasyonu tetikle
            transition={{ duration: 2.5, delay: index * 0.2 }}
          >
            <p>"{testimonial.feedback}"</p>
            <h4>- {testimonial.name}</h4>
          </motion.div>
        ))}
      </div>
    </section>
  );
}

export default Testimonials;
